import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  constructor(private http: HttpClient){}
  public renderImage: boolean = false
  public Eventos: any = [];

  ngOnInit(): void{
    this.getEventos();
    this.renderImageFunction();
  }

  public getEventos(): void{
    this.http.get('https://localhost:5001/Evento').subscribe(
      response => this.Eventos = response,
      error => console.log(error)
    )

  }

  public renderImageFunction(): void{
    if(this.renderImage == true){
      this.renderImage = false;
    }else if(this.renderImage == false){
      this.renderImage = true;
    }
  }
}
<h2>Eventos</h2>
<hr>

<form action="" class="form-inline">
  <div class="form-group  mb-2">
    <input type="text" class="form-control mr-2" placeholder="Buscar">
    <button class="btn btn-outline-success">Buscar</button>
  </div>
</form>

<table class="table table-striped">
  <thead class="bg-dark">
    <tr>
      <th>#</th>
      <th>Imagem <button class="btn btn-outline-success" >Ocult</button></th>
      <th>Tema</th>
      <th>Local</th>
      <th>Data</th>
      <th>Quantidade de pessoas</th>
      <th>Lote</th>
      <th>Opçoes</th>
    </tr>
  </thead>
  <tbody *ngIf="Eventos && Eventos.length">
    <tr *ngFor="let item of Eventos">
      <td>{{item.eventoId}}</td>
      <td><img *ngIf="!renderImage"  width="200px" src="{{item.imageUrl}}" alt="">  </td>
      <td>{{item.tema}}</td>
      <td>{{item.local}}</td>
      <td>{{item.dataEvento}}</td>
      <td>{{item.qtdPessoas}}</td>
      <td>{{item.lote}}</td>
      <td>{{item.opcoes}}</td>
    </tr>
  </tbody>
  <tfoot *ngIf="!Eventos.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum evento encontrado!</h4>
      </td>
    </tr>
  </tfoot>
</table>
